  switchNameHandler = () => {
    this.setState({
      persons : [
        {name: "Malik Waqar", age: 25},
        {name: "Muhammad Rizwan", age: 28},
        {name: "Umar Ali", age: 32}
      ]
    }
    )
  }

  nameChangedHandler = (event) => {
    this.setState({
      persons : [
        {name: "Malik Waqar", age: 25},
        {name: event.target.value, age: 28},
        {name: "Umar Ali", age: 32}
      ]
    }
    )
  }

  render() {
    return (
      <div className="App">
        <h1>Hi, I'm a React App!</h1>
        <button id="btn" onClick={this.switchNameHandler}>Switch Name</button>
        <Person name={this.state.persons[0].name} age={this.state.persons[0].age} />
        <Person name={this.state.persons[1].name} age={this.state.persons[1].age} changed={this.nameChangedHandler} />
        <Person name={this.state.persons[2].name} age={this.state.persons[2].age} />
        {/* <UserInput changename={this.usernameChangedHandler} changeage={this.ageChangedHandler} currentname={this.state.name} currentage={this.state.age} />
        <UserOutput name={this.state.name} age={this.state.age}  /> */}
      </div>
    );
  }

}

export default App;
